   const [news, setNews] = useState({ news: [] });
   const [newsList, setNewsList] = useState({ newsList: [] });
   const [categories, setCategories] = useState({ categories: {
                     business:[],
                     general: [],
                     entertainment: [],
                     sports: [],
                     health: [],
                     science: [],
                     technology: []
                  }});
   const [show, setShow] = useState(false);


   useEffect(() => {
      getNews_();
      getCategories();
     // getNews_();
